import React, { useEffect, useState } from 'react';
import './TicketList.css';

export interface Ticket {
    id: number;
    title: string;
    status: string;
    name?: string;
    email?: string;
    subject?: string;
    message?: string;
    telefone?: string;
}

interface TicketListProps {
    onSelect: (ticket: Ticket) => void;
}

const TicketList: React.FC<TicketListProps> = ({ onSelect }) => {
    const [tickets, setTickets] = useState<Ticket[]>([]);

    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_URL}/tickets_api.php`, { credentials: 'include' })
            .then(res => res.json())
            .then(data => {
                const mapped = (data || []).map((t: any, idx: number) => ({
                    id: idx + 1,
                    title: t.subject || t.title || 'Sem título',
                    status: t.status || 'Desconhecido',
                    name: t.name,
                    email: t.email,
                    subject: t.subject,
                    message: t.message,
                    telefone: t.telefone,
                }));
                setTickets(mapped);
            });
    }, []);

    return (
        <div className="ticket-list">
            <h2>Support Tickets</h2>
            <ul>
                {tickets.map(ticket => (
                    <li
                        key={ticket.id}
                        className={`ticket-item ${ticket.status.toLowerCase()}`}
                        onClick={() => onSelect(ticket)}
                        style={{ cursor: 'pointer' }}
                    >
                        <h3>{ticket.title}</h3>
                        <p>Status: {ticket.status}</p>
                        <p><strong>Nome:</strong> {ticket.name}</p>
                        <p><strong>Email:</strong> {ticket.email}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default TicketList;
