import React, { useEffect, useState } from 'react';
import './Dashboard.css';
import { FaLock, FaLockOpen } from 'react-icons/fa';

interface DashboardProps {
    refresh?: number;
    onTicketChange?: () => void;
}

const Dashboard: React.FC<DashboardProps> = ({ refresh }) => {
    const [stats, setStats] = useState({ open: 0, closed: 0 });
    const [period, setPeriod] = useState({ from: '', to: '' });
    // Placeholder for chart data
    // Fetch stats from API
    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_URL}/tickets_api.php`, { credentials: 'include' })
            .then(res => res.json())
            .then(data => {
                setStats({
                    open: data.filter((t: any) => t.status === 'nao_aberto' || t.status === 'Open').length,
                    closed: data.filter((t: any) => t.status === 'resolvido' || t.status === 'Closed').length,
                });
            });
    }, [refresh]);

    return (
        <div className="dashboard-v2" style={{padding: '24px 32px'}}>
            <div className="dashboard-header-v2" style={{display:'flex',justifyContent:'space-between',alignItems:'center',marginBottom:16}}>
                <div>
                    <h2 style={{margin:0,fontWeight:700}}>Dashboard</h2>
                    <div className="dashboard-breadcrumb" style={{color:'#888',fontSize:14,marginTop:2}}>Gerencial &gt; Dashboard</div>
                </div>
            </div>
            <div className="dashboard-filters-row" style={{display:'flex',alignItems:'center',gap:16,marginBottom:24}}>
                <select className="dashboard-select" style={{padding:'6px 12px',borderRadius:4,border:'1px solid #ccc'}}>
                    <option>Selecionar</option>
                </select>
                <div className="dashboard-period" style={{display:'flex',alignItems:'center',gap:8}}>
                    <label style={{fontWeight:500}}>Período:</label>
                    <input type="date" value={period.from} onChange={e => setPeriod(p => ({ ...p, from: e.target.value }))} style={{padding:'6px',borderRadius:4,border:'1px solid #ccc'}} />
                    <span style={{margin:'0 6px'}}>até</span>
                    <input type="date" value={period.to} onChange={e => setPeriod(p => ({ ...p, to: e.target.value }))} style={{padding:'6px',borderRadius:4,border:'1px solid #ccc'}} />
                    <button className="dashboard-filter-btn" style={{background:'#1976d2',color:'#fff',border:'none',padding:'6px 18px',borderRadius:4,cursor:'pointer',fontWeight:600}}>Filtrar</button>
                </div>
            </div>
            <div className="dashboard-cards-row" style={{display:'flex',gap:24,marginBottom:28}}>
                <div className="dashboard-card open" style={{flex:1,background:'#43b96e',color:'#fff',borderRadius:8,padding:'24px 18px',display:'flex',alignItems:'center',gap:18,boxShadow:'0 2px 8px #0001'}}>
                    <FaLockOpen size={36} />
                    <div style={{flex:1}}>
                        <div className="dashboard-card-label" style={{fontWeight:700,fontSize:18,letterSpacing:1}}>ABERTOS</div>
                        <div className="dashboard-card-value" style={{fontSize:28,fontWeight:800,marginTop:2}}>{stats.open}</div>
                    </div>
                </div>
                <div className="dashboard-card closed" style={{flex:1,background:'#d32f2f',color:'#fff',borderRadius:8,padding:'24px 18px',display:'flex',alignItems:'center',gap:18,boxShadow:'0 2px 8px #0001'}}>
                    <FaLock size={36} />
                    <div style={{flex:1}}>
                        <div className="dashboard-card-label" style={{fontWeight:700,fontSize:18,letterSpacing:1}}>FINALIZADOS</div>
                        <div className="dashboard-card-value" style={{fontSize:28,fontWeight:800,marginTop:2}}>{stats.closed}</div>
                    </div>
                </div>
            </div>
            <div className="dashboard-charts-row" style={{display:'flex',gap:24,marginBottom:24}}>
                <div className="dashboard-chart-box" style={{flex:1,background:'#fff',borderRadius:8,padding:'18px 18px 28px 18px',boxShadow:'0 2px 8px #0001',minHeight:180}}>
                    <div className="dashboard-chart-title" style={{fontWeight:600,marginBottom:10}}>Top 10 atendentes com chamados abertos</div>
                    <div className="dashboard-chart-placeholder" style={{color:'#888',fontSize:15}}>Nenhum registro foi encontrado</div>
                </div>
                <div className="dashboard-chart-box" style={{flex:1,background:'#fff',borderRadius:8,padding:'18px 18px 28px 18px',boxShadow:'0 2px 8px #0001',minHeight:180}}>
                    <div className="dashboard-chart-title" style={{fontWeight:600,marginBottom:10}}>Top produtos</div>
                    <div className="dashboard-chart-placeholder" style={{color:'#888',fontSize:15}}>[Gráfico de produtos]</div>
                </div>
            </div>
            <div className="dashboard-charts-row" style={{display:'flex',gap:24}}>
                <div className="dashboard-chart-box" style={{flex:1,background:'#fff',borderRadius:8,padding:'18px 18px 28px 18px',boxShadow:'0 2px 8px #0001',minHeight:180}}>
                    <div className="dashboard-chart-title" style={{fontWeight:600,marginBottom:10}}>Por prioridade</div>
                    <div className="dashboard-chart-placeholder" style={{color:'#888',fontSize:15}}>[Gráfico de prioridade]</div>
                </div>
                <div className="dashboard-chart-box" style={{flex:1,background:'#fff',borderRadius:8,padding:'18px 18px 28px 18px',boxShadow:'0 2px 8px #0001',minHeight:180}}>
                    <div className="dashboard-chart-title" style={{fontWeight:600,marginBottom:10}}>Por categoria</div>
                    <div className="dashboard-chart-placeholder" style={{color:'#888',fontSize:15}}>[Gráfico de categoria]</div>
                </div>
                <div className="dashboard-chart-box" style={{flex:1,background:'#fff',borderRadius:8,padding:'18px 18px 28px 18px',boxShadow:'0 2px 8px #0001',minHeight:180}}>
                    <div className="dashboard-chart-title" style={{fontWeight:600,marginBottom:10}}>Por tipo problema</div>
                    <div className="dashboard-chart-placeholder" style={{color:'#888',fontSize:15}}>[Gráfico de tipo problema]</div>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
