import React, { useEffect, useState } from 'react';
import './Dashboard.css';

interface ChatMessage {
    author: string;
    message: string;
    timestamp: string;
}

const Dashboard: React.FC = () => {
    const [stats, setStats] = useState({ total: 0, open: 0, closed: 0 });
    const [recent, setRecent] = useState<any[]>([]);
    const [chatTicketId, setChatTicketId] = useState<number | null>(null);
    const [chatMessages, setChatMessages] = useState<ChatMessage[]>([]);
    const [chatAuthor, setChatAuthor] = useState('');
    const [chatText, setChatText] = useState('');
    const [chatLoading, setChatLoading] = useState(false);

    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_URL}/tickets_api.php`, { credentials: 'include' })
            .then(res => res.json())
            .then(data => {
                setStats({
                    total: data.length,
                    open: data.filter((t: any) => t.status === 'nao_aberto' || t.status === 'Open').length,
                    closed: data.filter((t: any) => t.status === 'resolvido' || t.status === 'Closed').length,
                });
                setRecent(data.slice(-3).reverse());
            });
    }, []);

    const openChat = (ticketId: number) => {
        setChatTicketId(ticketId);
        setChatLoading(true);
        fetch(`${process.env.REACT_APP_API_URL}/chat.php?id=${ticketId}`)
            .then(res => res.json())
            .then(data => {
                setChatMessages(data);
                setChatLoading(false);
            });
    };

    const sendChat = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!chatTicketId || !chatAuthor || !chatText) return;
        setChatLoading(true);
        await fetch(`${process.env.REACT_APP_API_URL}/chat.php?id=${chatTicketId}`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ author: chatAuthor, message: chatText })
        });
        setChatText('');
        // reload chat
        openChat(chatTicketId);
    };

    return (
        <div className="dashboard">
            <header className="dashboard-header">
                <h1>Dashboard</h1>
            </header>
            <section className="dashboard-stats">
                <div className="stat-item">
                    <h2>Total Tickets</h2>
                    <p>{stats.total}</p>
                </div>
                <div className="stat-item">
                    <h2>Open Tickets</h2>
                    <p>{stats.open}</p>
                </div>
                <div className="stat-item">
                    <h2>Closed Tickets</h2>
                    <p>{stats.closed}</p>
                </div>
            </section>
            <section className="dashboard-summary">
                <h2>Recent Activity</h2>
                <ul>
                    {recent.map((t, i) => (
                        <li key={i}>
                            Ticket #{t.id || i + 1} - {t.status}
                            {t.name && <> | <b>{t.name}</b></>}
                            {t.subject && <> | {t.subject}</>}
                            <button style={{ marginLeft: 8 }} onClick={() => openChat(t.id)}>Chat</button>
                        </li>
                    ))}
                </ul>
                {chatTicketId && (
                    <div style={{ border: '1px solid #ccc', padding: 12, marginTop: 16, borderRadius: 8 }}>
                        <h3>Chat do Ticket #{chatTicketId}</h3>
                        {chatLoading ? <div>Carregando mensagens...</div> : (
                            <div style={{ maxHeight: 200, overflowY: 'auto', background: '#fafafa', padding: 8, marginBottom: 8 }}>
                                {chatMessages.length === 0 && <div>Nenhuma mensagem.</div>}
                                {chatMessages.map((msg, idx) => (
                                    <div key={idx} style={{ marginBottom: 6 }}>
                                        <b>{msg.author}</b> <span style={{ color: '#888', fontSize: 12 }}>[{msg.timestamp}]</span><br />
                                        {msg.message}
                                    </div>
                                ))}
                            </div>
                        )}
                        <form onSubmit={sendChat} style={{ display: 'flex', gap: 8 }}>
                            <input value={chatAuthor} onChange={e => setChatAuthor(e.target.value)} placeholder="Seu nome" required style={{ flex: 1 }} />
                            <input value={chatText} onChange={e => setChatText(e.target.value)} placeholder="Mensagem" required style={{ flex: 2 }} />
                            <button type="submit" disabled={chatLoading}>Enviar</button>
                        </form>
                        <button onClick={() => setChatTicketId(null)} style={{ marginTop: 8 }}>Fechar Chat</button>
                    </div>
                )}
            </section>
        </div>
    );
};

export default Dashboard;
